## Importing Librariesimport numpy as npimport matplotlib.pyplot as pltimport pandas as pd## Importing Datasetdataset = pd.read_csv('Position_Salaries.csv')## Index Independant and dependant valuesx = dataset.iloc[:, 1:-1].valuesy = dataset.iloc[:, -1].values## Training the Decision Tree Regression model on the whole datasetfrom sklearn.tree import DecisionTreeRegressorregressor = DecisionTreeRegressor(random_state = 0)regressor.fit(x, y)## Predicting a new result#pred = regressor.predict([[6.5]])#print(pred)## Visualising the Decision Tree Regression results (higher resolution)x_grid = np.arange(min(x), max(x), 0.01)x_grid = x_grid.reshape((len(x_grid), 1))plt.scatter(x, y, color = 'red')plt.plot(x_grid, regressor.predict(x_grid), color = 'blue')plt.title('Salary Expectations (Decision Tree Regression)')plt.xlabel('Position Level')plt.ylabel('Salary')plt.show()