# Importing Librariesimport numpy as npimport matplotlib.pyplot as pltimport pandas as pd# Importing Datasetdataset = pd.read_csv('Position_Salaries.csv')# Index Independant and dependant valuesx = dataset.iloc[:, 1:-1].valuesy = dataset.iloc[:, -1].valuesy =  y.reshape(len(y),1)# Feature Scalingfrom sklearn.preprocessing import StandardScalersc_x = StandardScaler()sc_y = StandardScaler()x = sc_x.fit_transform(x)y = sc_y.fit_transform(y)# Training the SVR model on the whole datasetfrom sklearn.svm import SVRregressor = SVR(kernel = 'rbf')regressor.fit(x, y.ravel())# Predicting a new result# sc_y.inverse_transform([regressor.predict(sc_x.transform([[6.5]]))])# Visualizing the SVR resultsm = regressor.predict(x)plt.scatter(sc_x.inverse_transform(x), sc_y.inverse_transform(y), color = 'red')plt.plot(sc_x.inverse_transform(x), sc_y.inverse_transform(m.reshape(len(m),1)), color = 'blue')plt.title('Salary Expectations (SVR)')plt.xlabel('Position level')plt.ylabel('Salary')