# Importing Librariesimport numpy as npimport matplotlib.pyplot as pltimport pandas as pd# Importing Datasetdataset = pd.read_csv('Salary_Data.csv')x = dataset.iloc[:, :-1].valuesy = dataset.iloc[:, -1].values# Splitting the dataset into Training set and Test setfrom sklearn.model_selection import train_test_splitx_train, x_test, y_train, y_test = train_test_split(x, y, test_size = 1/3, random_state = 0)# Training the Simple Linear Regression on the Training setfrom sklearn.linear_model import LinearRegressionregressor = LinearRegression()regressor.fit(x_train, y_train)# Predicting the Test set resultsy_pred = regressor.predict(x_test)# Visusualizing the Training set Resultsplt.scatter(x_train, y_train, color = 'red')plt.plot(x_train, regressor.predict(x_train), color = 'blue')plt.title('Salary vs. Experience (Training set)')plt.xlabel('Years of Experience')plt.ylabel('Salary ($)')plt.show()# Visusualizing the Test set Results#plt.scatter(x_test, y_test, color = 'red')#plt.plot(x_train, regressor.predict(x_train), color = 'blue')#plt.title('Salary vs. Experience (Test set)')#plt.xlabel('Years of Experience')#plt.ylabel('Salary ($)')#plt.show()# Making a single prediction#print(regressor.predict([[12]]))# Getting the final linear regression equation with the values of the coefficients#print(regressor.coef_)#print(regressor.intercept_)